<div id="inner_content"><?php if(isset($errors)) { 	echo View::factory('alert/errors')->set('errors', $errors)->render();}?></div><?php echo Form::open('auth/login'); ?><table class="form">    <tbody>        <tr>            <td>Username</td>            <td><?php echo Form::input('username'); ?></td>        </tr>        <tr>            <td>Password</td>            <td><?php echo Form::password('password'); ?></td>        </tr>    </tbody>    <tfoot>        <tr>            <td colspan="2"><?php echo Form::submit('submit', 'Login', array('class'=>'button')); ?></td>        </tr>    </tfoot></table><?php echo Form::close(''); ?><div id="inner_content">Forgot password? <?php echo Html::anchor('auth/forgot_password', 'Get a new one here'); ?></div>