<h1>Login</h1><?php if(isset($errors)) { 	echo View::factory('alert/errors')->set('errors', $errors)->render();}?><?php echo Form::open('auth/login'); ?><table>    <tbody>        <tr>            <td>Username</td>            <td><?php echo Form::input('username'); ?></td>        </tr>        <tr>            <td>Password</td>            <td><?php echo Form::password('password'); ?></td>        </tr>    </tbody>    <tfoot>        <tr>            <td colspan="2"><?php echo Form::submit('submit', 'Login'); ?></td>        </tr>    </tfoot></table><?php echo Form::close(''); ?>Forgot password? <?php echo Html::anchor('auth/forgot_password', 'Get a new one here'); ?>